mapper.select:
SELECT * FROM ssrolc.disclosure

	
		deptType IN 
		 
		       #{item}
		 
	
	
		And deptArea1 = #{deptArea1}
		
			And deptArea2 = #{deptArea2}
		
	
	
		And memName = #{memName}
	
	
		And regDate BETWEEN #{startDate} AND #{endDate}
	

ORDER BY regDate DESC

	LIMIT #{startLimit},#{endLimit}

SELECT count(0) FROM ssrolc.disclosure

	
		deptType IN 
		 
		       #{item}
		 
	
	
		And deptArea1 = #{deptArea1}
		
			And deptArea2 = #{deptArea2}
		
	
	
		And memName = #{memName}
	
	
		And regDate BETWEEN #{startDate} AND #{endDate}
